import React, { useState } from "react";
import { Menu, Home } from "lucide-react";
import { HashLink } from "react-router-hash-link";
import { useNavigate } from "react-router-dom";
import "./NavBar.css";
const logo = "/src/assets/images/logo.png";
// Import du logo

const NavBar: React.FC = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [active, setActive] = useState("Inici");
  const navigate = useNavigate();

  return (
    <nav className="navbar">
      {/* LOGO */}
      <div className="logo">
        <a href="/">
          <img src={logo} alt="Quetx Logo" />
        </a>
      </div>

      {/* MENU */}
      <ul className={`menu ${menuOpen ? "open" : ""}`}>
        <li>
          <a
            href="/"
            className={active === "Inici" ? "active" : ""}
            onClick={() => {
              setActive("Inici");
              setMenuOpen(false);
            }}
          >
            <Home size={16} style={{ marginRight: "8px" }} />
            Inici
          </a>
        </li>
        <li>
          <HashLink
            to="/#espais"
            smooth
            className={active === "Els Nostres Espais" ? "active" : ""}
            onClick={(e) => {
              e.preventDefault();
              setActive("Els Nostres Espais");
              setMenuOpen(false);
              navigate("/");
              setTimeout(() => {
                const section = document.getElementById("espais");
                if (section) {
                  section.scrollIntoView({ behavior: "smooth" });
                }
              }, 100);
            }}
          >
            Els Nostres Espais
          </HashLink>
        </li>
        <li>
          <a
            href="/qui-som"
            className={active === "Qui Som" ? "active" : ""}
            onClick={() => {
              setActive("Qui Som");
              setMenuOpen(false);
            }}
          >
            Qui Som
          </a>
        </li>
        <li>
          <a
            href="/actualitats"
            className={active === "Actualitats" ? "active" : ""}
            onClick={() => {
              setActive("Actualitats");
              setMenuOpen(false);
            }}
          >
            Actualitats
          </a>
        </li>
        <li>
          <a
            href="/contacte"
            className={active === "Contacte" ? "active" : ""}
            onClick={() => {
              setActive("Contacte");
              setMenuOpen(false);
            }}
          >
            Contacte
          </a>
        </li>
      </ul>

      {/* BOUTON MENU HAMBURGER */}
      <button className="burger" onClick={() => setMenuOpen(!menuOpen)}>
        <Menu size={24} />
      </button>
    </nav>
  );
};

export default NavBar;
