import React, { useState } from "react";
import "./NavBar.css";
const logo = "/src/assets/images/logo.png";
// Import du logo

const NavBar: React.FC = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [active, setActive] = useState("Inici");

  return (
    <nav className="navbar">
      {/* LOGO */}
      <div className="logo">
        <a href="/">
          <img src={logo} alt="Quetx Logo" />
        </a>
      </div>

      {/* MENU */}
      <ul className={`menu ${menuOpen ? "open" : ""}`}>
        <li>
          <a
            href="/"
            className={active === "Inici" ? "active" : ""}
            onClick={() => {
              setActive("Inici");
              setMenuOpen(false);
            }}
          >
            Inici
          </a>
        </li>
        <li>
          <a
            href="/espais"
            className={active === "Els Nostres Espais" ? "active" : ""}
            onClick={() => {
              setActive("Els Nostres Espais");
              setMenuOpen(false);
            }}
          >
            Els Nostres Espais
          </a>
        </li>
        <li>
          <a
            href="/qui-som"
            className={active === "Qui Som" ? "active" : ""}
            onClick={() => {
              setActive("Qui Som");
              setMenuOpen(false);
            }}
          >
            Qui Som
          </a>
        </li>
        <li>
          <a
            href="/contacte"
            className={active === "Contact" ? "active" : ""}
            onClick={() => {
             setActive("Contact");
              setMenuOpen(false);
            }}
         >
            Contact
          </a>
        </li>
      </ul>

      {/* BOUON MENU HAMBURGER */}
      <button className="burger" onClick={() => setMenuOpen(!menuOpen)}>
        â˜°
      </button>
    </nav>
  );
};

export default NavBar;
