import React, { useState, useEffect } from "react";
import { Home, Info } from "lucide-react";

interface PropertyCardProps {
  title: string;
  subtitle: string;
  description: string;
  images: string[]; // [main, img1, img2, ...]
  link: string;
}

const PropertyCard: React.FC<PropertyCardProps> = ({
  title,
  subtitle,
  description,
  images,
  link,
}) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % (images.length - 1));
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % (images.length - 1));
  };

  const prevImage = () => {
    setCurrentIndex((prevIndex) =>
      (prevIndex - 1 + (images.length - 1)) % (images.length - 1)
    );
  };

  return (
    <div className="property-card" style={{ display: "flex", gap: "2rem" }}>
      {/* Gauche : grande image */}
      <div className="property-image" style={{ flex: 1 }}>
        <img
          src={images[0]}
          alt="Main"
          className="main-image"
          style={{ width: "100%", height: "auto", borderRadius: "10px" }}
        />
      </div>

      {/* Droite : miniatures + texte */}
      <div className="property-details" style={{ flex: 1, display: "flex", flexDirection: "column", justifyContent: "space-between" }}>
        {/* Miniatures */}
        <div className="thumbnails" style={{ display: "flex", gap: "0.5rem" }}>
          <img
            src={images[currentIndex + 1]}
            alt="Mini 1"
            style={{ width: "50%", height: "100px", objectFit: "cover", borderRadius: "8px" }}
          />
          <img
            src={images[(currentIndex + 2) % (images.length - 1) + 1]}
            alt="Mini 2"
            style={{ width: "50%", height: "100px", objectFit: "cover", borderRadius: "8px" }}
          />

          {/* Fl√®ches */}
          <button onClick={prevImage} style={arrowStyleLeft}>
            <i className="fas fa-chevron-left"></i>
          </button>
          <button onClick={nextImage} style={arrowStyleRight}>
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>

        {/* Texte */}
        <div style={{ marginTop: "1rem" }}>
          <h2>
            <Home size={16} style={{ marginRight: "8px" }} /> {title}
          </h2>
          <h3>{subtitle}</h3>
          <p>{description}</p>
          <a
            href={link}
            className="discover-button"
            style={{
              backgroundColor: "#c59d6b",
              color: "white",
              padding: "0.6rem 1.2rem",
              borderRadius: "30px",
              display: "inline-block",
              marginTop: "0.5rem",
              textDecoration: "none",
            }}
          >
            <Info size={16} style={{ marginRight: "8px" }} /> Descobriu
          </a>
        </div>
      </div>
    </div>
  );
};

const arrowStyleLeft: React.CSSProperties = {
  position: "absolute",
  left: "-15px",
  top: "50%",
  transform: "translateY(-50%)",
  backgroundColor: "#c59d6b",
  opacity: 0.8,
  borderRadius: "50%",
  padding: "10px",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const arrowStyleRight: React.CSSProperties = {
  position: "absolute",
  right: "-15px",
  top: "50%",
  transform: "translateY(-50%)",
  backgroundColor: "#c59d6b",
  opacity: 0.8,
  borderRadius: "50%",
  padding: "10px",
  color: "white",
  border: "none",
  cursor: "pointer",
};

export default PropertyCard;
