import React, { useState, useEffect } from "react";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";
import "./ProperyCard.css";

interface PropertyCardProps {
  title: string;
  subtitle: string;
  description: string;
  images: string[];
  link: string;
}

const PropertyCard: React.FC<PropertyCardProps> = ({
  title,
  subtitle,
  description,
  images,
  link,
}) => {
  const [currentImage, setCurrentImage] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  const nextImage = () => {
    setCurrentImage((prevImage) => (prevImage + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImage(
      (prevImage) => (prevImage - 1 + images.length) % images.length
    );
  };

  return (
    <div className="property-card">
      {/* Left Column: Main Image */}
      <div className="left-column">
        <img
          className="main-image"
          src={images[0] || "https://via.placeholder.com/400"}
          alt={title}
        />
      </div>

      {/* Right Column */}
      <div className="right-column">
        {/* Top: Thumbnails with navigation */}
        <div className="thumbnails">
          <button className="arrow-button left" onClick={prevImage}>
            <FaArrowLeft />
          </button>
          <img
            className="miniature"
            src={images[currentImage] || "https://via.placeholder.com/200"}
            alt={`${title} thumbnail`}
          />
          <button className="arrow-button right" onClick={nextImage}>
            <FaArrowRight />
          </button>
        </div>

        {/* Bottom: Text and Button */}
        <div className="text-block">
          <h2>{title}</h2>
          <h3>{subtitle}</h3>
          <p>{description}</p>
          <a href={link} className="discover-button">
            Descobriu
          </a>
        </div>
      </div>
    </div>
  );
};

export default PropertyCard;
