import React, { useState } from "react";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

// Propriétés pour le bien immobilier
interface PropertyCardProps {
  title: string;
  description: string;
  images: string[];
  link: string;
}

const PropertyCard: React.FC<PropertyCardProps> = ({
  title,
  description,
  images,
  link,
}) => {
  const [currentImage, setCurrentImage] = useState(0);

  // Fonction pour changer l'image du diaporama
  const nextImage = () => {
    setCurrentImage((prevImage) => (prevImage + 1) % images.length); // Passe à l'image suivante
  };

  const prevImage = () => {
    setCurrentImage(
      (prevImage) => (prevImage - 1 + images.length) % images.length
    ); // Passe à l'image précédente
  };

  return (
    <div className="property-card">
      {/* Diaporama */}
      <div className="property-images">
        <img src={images[currentImage]} alt={title} />
        <div className="carousel-controls">
          {/* Boutons de navigation */}
       
        </div>
      </div>

      {/* Texte */}
      <div className="property-info">
        <h2>{title}</h2>
        <p>{description}</p>
        <a href={link} className="discover-button">
          Descobriu
        </a>
      </div>
    </div>
  );
};

export default PropertyCard;
