import React, { useState } from "react";
import { Home, Tag, Info } from "lucide-react";

// Propriétés pour le bien immobilier
interface PropertyCardProps {
  title: string;
  description: string;
  images: string[];
  link: string;
}

const PropertyCard: React.FC<PropertyCardProps> = ({
  title,
  description,
  images,
  link,
}) => {
  const [currentImage, setCurrentImage] = useState(0);

  // Fonction pour changer l'image du diaporama
  const nextImage = () => {
    setCurrentImage((prevImage) => (prevImage + 1) % images.length); // Passe à l'image suivante
  };

  const prevImage = () => {
    setCurrentImage(
      (prevImage) => (prevImage - 1 + images.length) % images.length
    ); // Passe à l'image précédente
  };

  return (
    <div className="property-card">
      <div className="property-images-container">
        {/* Grande image statique à gauche */}
        <div className="property-image">
          <img
            src={images[0] || "https://via.placeholder.com/300x200"} // Placeholder image
            alt={title || "Image de propriété"}
            className="main-image"
          />
        </div>

        {/* Diaporama des miniatures à droite */}
        <div className="property-thumbnail">
          <img
            src={images[currentImage] || "https://via.placeholder.com/100x100"} // Placeholder thumbnail
            alt={title || "Miniature de propriété"}
            className="thumbnail-image"
          />
          <div className="carousel-controls">
            {/* Boutons de navigation */}
            <button className="arrow-button" onClick={prevImage}>
              <i
                className="fas fa-chevron-left"
                style={{ fontSize: "30px" }}
              ></i>
            </button>
            <button className="arrow-button" onClick={nextImage}>
              <i
                className="fas fa-chevron-right"
                style={{ fontSize: "30px" }}
              ></i>
            </button>
          </div>
        </div>
      </div>

      {/* Texte */}
      <div className="property-info">
        <h2>
          <Home size={16} style={{ marginRight: "8px" }} />
          {title}
        </h2>
        <h3>
          <Tag size={16} style={{ marginRight: "8px" }} />
          Localisation
        </h3>
        <p>{description}</p>
        <a href={link} className="discover-button">
          <Info size={16} style={{ marginRight: "8px" }} />
          Descobriu
        </a>
      </div>
    </div>
  );
};

export default PropertyCard;
